mapper.select:
 
		SELECT * 
		FROM homesitterlist 
		WHERE hsl_size = #{hsl_size} AND hsl_size = #{hsl_size} AND hsl_size = #{hsl_size} AND hsl_size = #{hsl_size} AND hsl_size = #{hsl_size} AND hsl_size = #{hsl_size}

SELECT *
FROM homesitterlist HSL LEFT JOIN homesitter HS ON HSL.HS_NUM = HS.HS_NUM 
	 LEFT JOIN homesitterlist_image HSI ON hsl.hsl_num = hsi.hsl_num

	
		or hsl_size = #{hsl.hsl_size}
	
	
		or hsl_service_type = #{hsl.hsl_service_type}
	
	
		or hsl_pet_age = #{hsl.hsl_pet_age}
	
	
		or hsl_chkin >= #{hsl.hsl_chkin} and hsl_chkout <= #{hsl.hsl_chkout}
	
	
		
	    	hsl_address like ('%${item}%')
	    	
	
	
		
			or hsl_service_type = 'allday'
