mapper.update:
update homesitterlist
set hsl_disdates = #{hsl_disdates}
where hsl_num = #{hsl_num}

update homesitterlist
set hsl_address = #{hsl_address},
	hsl_d_address = #{hsl_d_address}
where hs_num = #{hs_num}

update homesitter
set hs_filename = #{hs_filename}	
where hs_num = #{hs_num}
