mapper.update:
update homesitterlist
set hsl_disdates = #{hsl_disdates}
where hsl_num = #{hsl_num}
and hs_num=#{hs_num}

mapper.select:
select hs.hs_num, hs.hs_name, hs.hs_sex, hs.hs_birth, hs.hs_contact, hs.hs_address, hs.hs_d_address, hs.hs_email, hsli.hsl_title, hsli.hsl_comment, hsli.hsl_size, hsli.hsl_care_place, hsli.hsl_subway, hsli.hsl_yard, hsli.hsl_baby, hsli.hsl_family, hsli.hsl_service_type, hsli.hsl_pet_age, hsli.hs_disdate_num, hsli.hsl_price, hsli.hsl_chkin_str_time, hsli.hsl_chkin_end_time, hsli.hsl_chkout_str_time, hsli.hsl_chkout_end_time, hsli.hsl_status, hsd.hsd_disabledate, hsi.hsl_img_filename from homesitter hs left outer join homesitterlist hsli on hs.hs_num = hsl.hs_num left outer join homesitterdisabledate hsd on hs.hs_num = hsd.hs_num left outer join homesitterlist_image hsi on hsl.hsl_num = hsi.hsl_num
AND hsl_size like ('%${hsl.hsl_size}%')
AND hsl_service_type like ('%${hsl.hsl_service_type}%')
AND hsl_pet_age like ('%${hsl.hsl_pet_age}%')
AND hsli.hsl_chkin <= #{hsl.hsl_chkin}
AND hsli.hsl_chkout >= #{hsl.hsl_chkout}
and hsli.hsl_address in (select hsl_address from homesitterlist where
hsl_address like ('%${item}%')
)

mapper.update:
update homesitter
set hs_filename = #{hs_filename}	
where hs_num = #{hs_num}
